import React,{useState, useEffect} from 'react'

const CommentChart = ({comments}) => {

const [xvalues,setXvalues]=useState([1,2,3,4]);
const [yvalues, setYvalues] = useState([100,500,200,300]);

useEffect(()=>{
    new Chart("myChart", {
      type: "line",
      data: {
        labels: xvalues,
        datasets: [
          {
            data: yvalues,
            borderColor: "red",
            fill: false,
          }
        ]
      },
      options: {
        legend: { display: false },
      },
    });
},[]);



    return (
      <div>
        <canvas id="myChart" style={{width:'100%',maxWidth:'600px'}} ></canvas>
      </div>
    );
}

export default CommentChart
