import React,{useState, useEffect} from 'react'
import {Line} from 'react-chartjs-2'

const CommentChart = ({comments}) => {

const [datas,setData]=useState({});


useEffect(()=>{
    let grp={};
    
    comments.forEach(comment => {
        console.log("s-",comment);
        const d=new Date(comment.date);
        let temp=d.getFullYear().toString()+'/'+(d.getMonth()+1).toString()+'/'+d.getDate().toString();
        
        console.log("Date t-", temp);
       // grp[temp]=1
        
        console.log("grp-", grp);
        if (grp && grp[temp]) {
            grp[temp] = grp[temp]+1;
          console.log("t-", grp);
        } else {
            grp[temp] = 1;
          console.log("f-", grp);
        }
    });
console.log('Final',grp[2021/5/16]);
    setData({
      data: {
        labels: [1, 2, 3, 4, 5],
        
        datasets: [
          {
            label: "No of Comments",
            data: [100.2, 300, 200, 500],
            backgroundColor: "rgb(255, 99, 132)",
            borderColor: "rgba(255, 99, 132, 0.2)",
            fill: false,
          },
        ],
      },
      options: { legend: { display: true } },
    });


},[]);



    return <Line {...datas} />;
}

export default CommentChart
