import { GET_POSTS, POST_ERROR,UPDATE_LIKES } from '../action/type'

const initialstate={
    posts:[],
    post:null,
    loading:true,
    error:null
}

export default function post (state=initialstate,action){
    const {type,payload} = action;

    switch(type){
    case GET_POSTS:
        return{
            ...state, posts:payload,loading:false
        }
    case POST_ERROR:
         return {
           ...state,
           error: payload,
           loading: false,
           
         };  
    case UPDATE_LIKES:
        
        return {
          ...state,
          posts: state.posts.map((post) => {
              console.log('red_1',payload);
              console.log("red",
                post._id === payload.id
                  ? { ...post, likes: payload.likes }
                  : post
              );
           return post._id === payload.postID ? { ...post, likes: payload.likes } : post;
          }),
          loading:false
        };
              
    default:
        return state;     
    }

}
