import { GET_PROFILE, PROFILE_ERROR, LOGOUT,UPDATE_PROFILE } from "../action/type";

const initialstate = {
  profile: null,
  profiles: [],
  repos: [],
  loading: true,
  error: {},
};

export default function profile(state = initialstate, action) {
  const { type, payload } = action;
  switch (type) {
      case GET_PROFILE:
       case UPDATE_PROFILE: 
          return ({...state,profile:payload,loading:false,error:{}});

      case PROFILE_ERROR:
        
          return { ...state, error: payload, loading: false,profile:null }; 
      case LOGOUT:
        return { ...state, error: {}, loading: false, profile: null,repos:[] };    

      default:
          return state;      
  }
}
