import React,{useState, useEffect} from 'react'
import {Line} from 'react-chartjs-2'

const CommentChart = ({comments}) => {
console.log('start--',comments);
const [datas,setData]=useState({});


useEffect(() => {
  let grp = {};

  comments.forEach((comment) => {
    console.log("s-", comment);
    const d = new Date(comment.date);
    let temp =
      d.getFullYear().toString() +
      "/" +
      (d.getMonth() + 1).toString() +
      "/" +
      d.getDate().toString();

    console.log("Date t-", temp);
    // grp[temp]=1

    console.log("grp-", grp);
    if (grp && grp[temp]) {
      grp[temp] = grp[temp] + 1;
      console.log("t-", grp);
    } else {
      grp[temp] = 1;
      console.log("f-", grp);
    }
  });
  console.log("key-", Object.keys(grp));
  console.log("val-", Object.values(grp));
  setData({
    data: {
      labels: Object.keys(grp),

      datasets: [
        {
          label: "No of Comments",
          data: Object.values(grp),
          backgroundColor: "rgb(255, 99, 132)",
          borderColor: "rgba(255, 99, 132, 0.2)",
          fill: false,
        },
      ],
    },
    options: { legend: { display: true } },
  });
}, [comments]);



    return <Line {...datas} />;
}

export default CommentChart
