import React,{useEffect} from 'react'
import {connect} from 'react-redux'
import Spinner from '../layout/spinner'
import { getRepos } from "../../action/profile";
import spinner from '../layout/spinner';

const ProfileGithub = ({ getRepos ,username,repos}) => {
    useEffect(() => {
      getRepos(username);
    }, [getRepos]);
  return <div className="profile-github" >
      <h2 className="text-primary my-1">Gitub Repos</h2>
      {repos===null ? <Spinner /> : (
          repos.map((repo)=>{
            <div key={repo._id} className="repo bg-white p-1 my-1" >
                <div>
                    <h4>
                        <a href={repo.htm_url} target="_blank" rel="noopener noreferrer" >
                            {repo.name}
                        </a>
                    </h4>
                    </div>
            </div> 
          })
      ) }

  </div>;
};

const mapStatetoProps =(state)=>{

    return{repos:state.profile.repos}

}
export default connect(null, { getRepos })(ProfileGithub);
