import React,{useEffect,Fragment} from 'react'
import {connect} from 'react-redux'
import Spinner from  '../layout/spinner'
import { getProfilebyID } from '../../action/profile'
import {Link} from 'react-router-dom'

const Profile = ({ getProfilebyID,auth,profile:{profile,loading},match }) => {
useEffect(() => {
    
  getProfilebyID(match.params.id);
}, [getProfilebyID]);

  return <Fragment>
      {profile===null ? <Spinner /> : <Fragment>
        <Link to="/profiles" >
        GO back to Profiles
        </Link>
        {auth.isAuthendicated && auth.loading===false && auth.user._id===profile.user._id && (
            <Link to='/edit-profile' className="btn btn-dark" >
            Edit Profile
            </Link>
        )}
      </Fragment> }
  </Fragment>
};

const mapStatetoProps = (state)=>{
return{ 
    profile:state.profile,
    auth:state.auth
}
}

export default connect(mapStatetoProps,  { getProfilebyID })(Profile);
