import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  USER_LOADED,
  AUTH_ERROR,
} from "../action/type";

const initialState={
    token:localStorage.getItem('token'),
    isAuthendicated: null,
    loading : true,
    user:null
}

export default function auth (state=initialState,action){
    const {type,payload}= action;
    switch(type)
    {
        case USER_LOADED:
            return {
              ...state,
              isAuthendicated: true,
              loading: false,
              user:payload
            };
        case REGISTER_SUCCESS:
            localStorage.setItem('token',payload.token);
            return({
                ...state,
                ...payload,
                isAuthendicated:true,
                loading:false
            });
        case AUTH_ERROR:
        case REGISTER_FAIL:
            localStorage.removeItem('token');
             return {
               ...state,
              
               token:null,
               isAuthendicated: false,
               loading: false,
             };
        default:
            return state;     
    }

}
